script:
frappe.ui.form.on("Sales Invoice", {
    before_cancel: function(frm) {
        validated = false;
        frappe.prompt([{
                "label": "Tipo de Anulacion",
                "fieldname": "opts",
                "fieldtype": "Select",
                "reqd": 1,
                "options": 
                    "01 Deterioro de Factura Pre-Impresa\n" +
                    "02 Errores de Impresión(Factura Pre-Impresa)\n" +
                    "03 Impresión defectuosa\n" +
                    "04 Duplicidad de Factura\n" +
                    "05 Corrección de la Información\n" +
                    "06 Cambio de Productos\n" +
                    "07 Devolución de Productos\n" +
                    "08 Omisión de Productos\n" +
                    "09 Errores en Secuencias NCF\n"
            }],
            update_fields, "Elija el motivo de la cancelacion", "Continuar");

        function update_fields(response) {
            cur_frm.doc.tipo_de_anulacion = response.opts;
            cur_frm._save("Update", function() {
                cur_frm.save("Cancel");
            });

        }
    }
});

cur_frm.amend_doc = function() {
    var fn = function(newdoc) {
        newdoc.amended_from = "";
        newdoc.tipo_de_anulacion = "";
        refresh_field("amended_from");
    }
    cur_frm.copy_doc(fn)
}
//Apending Custom Script for the navigation buttons
frappe.ui.form.on('Sales Invoice', "refresh", function(frm){
    var callback = function(response){
        if(frm.doc.__islocal || !response.message) return ;

        var list = response.message;
        var index = 0, prev_index = 0, next_index = 0;
        var cur_route, prev_route, next_route;

        for( ; index < list.length; index ++){
            prev_index = index - 1 < 0 ? 0 : index - 1;
            next_index = index + 1 >= list.length ? list.length - 1 : index + 1;

            if(frm.doc.name == list[index].name){
                prev_route = list[prev_index].name;
                next_route = list[next_index].name;
                cur_route = list[index].name;

                break;
            }
        }

        var route_next = function(res){ set_emp_route(next_route); };
        var route_prev = function(res){ set_emp_route(prev_route); };
        var set_emp_route = function(docname){ frappe.set_route("Form/Sales Invoice",docname); };

        if(prev_route != cur_route) frm.add_custom_button("<< Prev", route_prev);

        if(next_route != cur_route) frm.add_custom_button("Next >>", route_next);
    };

    frappe.call({
        "method" : "heladom.white_listed.get_list",
        "args" : { 
            "doctype": frm.doctype, 
            "docname": frm.docname
        },
        "callback" : callback
    });
});

