frappe.ui.form.on("Payment Entry", {
    party: function(frm) {
        cur_frm.doc.party_name = "";
        refresh_field("party_name");

        if (frm.doc.party_type == "Customer") {
            frappe.db.get_value("Customer", {
                "name": frm.doc.party,
            }, "customer_name", callback);
        } else if (frm.doc.party_type == "Supplier") {
            frappe.db.get_value("Supplier", {
                "name": frm.doc.party,
            }, "supplier_name", callback);
        }
    }
});

var callback = function(response) {
    if (!response) return 0;

    if (cur_frm.doc.party_type == "Customer") {
        cur_frm.doc.party_name = response.customer_name;
    } else if (cur_frm.doc.party_type == "Supplier") {
        cur_frm.doc.party_name = response.supplier_name;
    }

    refresh_field("party_name");
}

