frappe.ui.form.on('Purchase Order', {
    onload_post_render: function(frm) {
        frm.set_query("generico", "items", function() {
            return {
                "filters": {
                    "es_generico": "1"
                }
            }
        })

        frm.set_query("item_code", "items", function(frm, cdt, cdn) {
            var row = locals[cdt][cdn]

            var filters = { 
                "es_generico": "0",
                "generico": row.generico || " "
            }

            if (me.frm.doc.is_subcontracted == "Yes") {

                $.extend(filters, {
                    'is_sub_contracted_item': 1
                })
            } else {
                $.extend(filters, {
                    'is_purchase_item': 1
                })
            }

            return {  
                query: "erpnext.controllers.queries.item_query", 
                "filters": filters  
            }
        })
    },
    refresh: function(frm) {
        var callback = function(response) {
            if (frm.doc.__islocal || !response.message) return;

            var list = response.message;
            var index = 0,
                prev_index = 0,
                next_index = 0;
            var cur_route, prev_route, next_route;

            for (; index < list.length; index++) {
                prev_index = index - 1 < 0 ? 0 : index - 1;
                next_index = index + 1 >= list.length ? list.length - 1 : index + 1;

                if (frm.doc.name == list[index].name) {
                    prev_route = list[prev_index].name;
                    next_route = list[next_index].name;
                    cur_route = list[index].name;

                    break;
                }
            }

            var route_next = function(res) {
                set_emp_route(next_route);
            };
            var route_prev = function(res) {
                set_emp_route(prev_route);
            };
            var set_emp_route = function(docname) {
                frappe.set_route("Form/Purchase Order", docname);
            };

            if (prev_route != cur_route) frm.add_custom_button("<< Prev", route_prev);

            if (next_route != cur_route) frm.add_custom_button("Next >>", route_next);
        };

        frappe.call({
            "method": "heladom.white_listed.get_list",
            "args": {
                "doctype": frm.doctype,
                "docname": frm.docname
            },
            "callback": callback
        });
    },
})

frappe.ui.form.on("Purchase Order Item", {
item_code: function(form, child_doctype, child_name) {
var row = locals[child_doctype][child_name]

if (row.item_code && !row.generico) {
frappe.msgprint("¡Debe seleccionar un Generico antes!")

setTimeout(function() {
form.fields_dict.items.grid.get_grid_row(child_name)
.on_grid_fields_dict.item_code.set_model_value(undefined)
}, 999)
}
}
})

