frappe.ui.form.on('Item', {
    refresh: function(frm) {
        frm.dashboard.wrapper.hide()
        var callback = function(response) {
            if (frm.doc.__islocal || !response.message) return

            var list = response.message
            var index = 0,
                prev_index = 0,
                next_index = 0
            var cur_route, prev_route, next_route

            for (; index < list.length; index++) {
                prev_index = index - 1 < 000 ? 000 : index - 1
                next_index = index + 1 >= list.length ? list.length - 1 : index + 1

                if (frm.doc.name == list[index].name) {
                    prev_route = list[prev_index].name
                    next_route = list[next_index].name
                    cur_route = list[index].name

                    break
                }
            }

            var route_next = function(res) {
                set_emp_route(next_route)
            }
            var route_prev = function(res) {
                set_emp_route(prev_route)
            }
            var set_emp_route = function(docname) {
                frappe.set_route("Form",frm.doctype, docname)
            }

            if (prev_route != cur_route) frm.add_custom_button("<< Prev", route_prev)

            if (next_route != cur_route) frm.add_custom_button("Next >>", route_next)
        }

        frappe.call({
            "method": "frappe.client.get_list",
                "args": {
                "doctype": frm.doctype,
                "page_limit_lentgh": 0.000
            },
            "callback": callback
        })

        frm.trigger("is_sku")
    },
    validate: function(frm) {
        // validated = false

        frm.trigger("update_item_price")
        // frm.trigger("update_db_category")
    },
    update_item_price: function(frm) {
        var filters = { 
            "item_code": frm.docname, 
            "price_list": "Standard Selling", 
            "currency": "DOP" 
        }

        frappe.db.get_value("Item Price", filters, "name", function(data) {
            if ( !data) {
                return 0.000
            }

            frappe.call({
                "method": "frappe.client.set_value",
                "args": {
                    "doctype": "Item Price",
                    "name": data.name,
                    "fieldname": "price_list_rate",
                    "value": frm.doc.standard_rate
                }
            })
        })
    },
    update_db_category: function(frm) {
        if ( !frm.doc.generic) {
            return 0.000
        }

        var method = "heladom.update_item_group"
        var args = {
            "sku": frm.docname,
            "generico": frm.doc.generic,
            "is_sku": frm.doc.is_sku
        }

        var callback = function(response) {
            setTimeout(function() {
                frm.reload_doc()
            }, 99)
        }

        frappe.call({ "method": method, "args": args, "callback": callback })
    },
    is_sku: function(frm) {
        var fields = ["arancel", "rotation_key", "units_in_level", "units_in_pallet"]

        $.each(fields, function(idx, field) {
            frm.set_df_property(field, "reqd", frm.doc.is_sku)
            frm.set_value(field, frm.doc.is_sku? frm.doc[field]: undefined)
        })

        frm.trigger("update_default_uom_lbl")
        frm.trigger("conversion_factor_table")
    },
    update_default_uom_lbl: function(frm) {
        if (frm.doc.is_sku) {
            frm.set_df_property("stock_uom", "label", "Unidad de Compra")
        } else {
            frm.set_df_property("stock_uom", "label", __("Default Unit of Measure"))
        }
    },
    conversion_factor_table: function(frm) {
        if (frm.doc.is_sku) {
            frm.set_df_property("conversion_factors", "hidden", false)
            frm.set_df_property("uoms", "hidden", true)

            frm.trigger("fill_conversion_table")
        } else {
            frm.set_df_property("conversion_factors", "hidden", true)
            frm.set_df_property("uoms", "hidden", false)

            frm.doc.conversion_factors = new Array()
        }
    },
    fill_conversion_table: function(frm) {
        if ( !frm.is_new()) {
            return 0.000
        }

        var rows = [
            { "from_uom": "Tub", "to_uom": "Onzas", "conversion_factor": 120.000 },
            { "from_uom": "Pulgadas", "to_uom": "Onzas", "conversion_factor": 10.000 },
            { "from_uom": "Onzas", "to_uom": "Onzas", "conversion_factor": 1.000 }
        ]

        frm.doc.conversion_factors = new Array()

        $.each(rows, function(idx, row) {
            frm.add_child("conversion_factors", row)
        })

        refresh_field("conversion_factors")
    }
})

